        default {
            Write-Host "Invalid option" -ForegroundColor Red
            Start-Sleep -Seconds 1
            Show-Menu
        }
    }
}

# ìŠ¤í¬ë¦½íŠ¸ ì‹¤í–‰
if ($PSBoundParameters.Count -eq 0) {
