class GlobalCountdown {
    constructor() {
        this.targetDate = null;
        this.eventName = '';
        this.countdownInterval = null;
        this.timezoneInterval = null;
        this.isActive = false;

        this.initializeElements();
        this.bindEvents();
        this.startTimezoneUpdates();
        this.setDefaultDate();
    }

    initializeElements() {
        // Input elements
        this.eventNameInput = document.getElementById('event-name');
        this.targetDateInput = document.getElementById('target-date');
        this.startBtn = document.getElementById('start-countdown');
        this.resetBtn = document.getElementById('reset-countdown');

        // Display elements
        this.countdownSection = document.getElementById('countdown-section');
        this.displayEventName = document.getElementById('display-event-name');
        this.displayEventDate = document.getElementById('display-event-date');

        // Time display elements
        this.daysEl = document.getElementById('days');
        this.hoursEl = document.getElementById('hours');
        this.minutesEl = document.getElementById('minutes');
        this.secondsEl = document.getElementById('seconds');
        this.progressFill = document.getElementById('progress-fill');

        // Timezone elements
        this.timezoneElements = {
            seoul: document.getElementById('seoul-time'),
            tokyo: document.getElementById('tokyo-time'),
            newyork: document.getElementById('newyork-time'),
            london: document.getElementById('london-time'),
            paris: document.getElementById('paris-time'),
            sydney: document.getElementById('sydney-time')
        };
    }

    bindEvents() {
        this.startBtn.addEventListener('click', () => this.startCountdown());
        this.resetBtn.addEventListener('click', () => this.resetCountdown());

        // Enter key support
        this.eventNameInput.addEventListener('keypress', (e) => {
            if (e.key === 'Enter') this.startCountdown();
        });

        this.targetDateInput.addEventListener('keypress', (e) => {
            if (e.key === 'Enter') this.startCountdown();
        });

        // Auto-save inputs to localStorage
        this.eventNameInput.addEventListener('input', () => {
            localStorage.setItem('countdown-event-name', this.eventNameInput.value);
        });

        this.targetDateInput.addEventListener('change', () => {
            localStorage.setItem('countdown-target-date', this.targetDateInput.value);
        });

        // Load saved inputs
        this.loadSavedInputs();
    }

    loadSavedInputs() {
        const savedEventName = localStorage.getItem('countdown-event-name');
        const savedTargetDate = localStorage.getItem('countdown-target-date');

        if (savedEventName) {
            this.eventNameInput.value = savedEventName;
        }

        if (savedTargetDate) {
            this.targetDateInput.value = savedTargetDate;
        }
    }

    setDefaultDate() {
        // Set default to New Year 2025 if no saved date
        if (!this.targetDateInput.value) {
            const newYear = new Date('2025-01-01T00:00:00');
            const localDateTime = new Date(newYear.getTime() - newYear.getTimezoneOffset() * 60000);
            this.targetDateInput.value = localDateTime.toISOString().slice(0, 16);
        }
    }

    startCountdown() {
        const eventName = this.eventNameInput.value.trim() || '새해 2025';
        const targetDateValue = this.targetDateInput.value;

        if (!targetDateValue) {
            this.showNotification('목표 날짜를 선택해주세요!', 'error');
            this.targetDateInput.focus();
            return;
        }

        this.targetDate = new Date(targetDateValue);
        this.eventName = eventName;

        // Validate target date
        if (this.targetDate <= new Date()) {
            this.showNotification('미래의 날짜를 선택해주세요!', 'error');
            this.targetDateInput.focus();
            return;
        }

        this.isActive = true;
        this.updateDisplay();
        this.startCountdownTimer();

        // Show countdown section with animation
        this.countdownSection.classList.add('active');
        this.countdownSection.scrollIntoView({ behavior: 'smooth', block: 'center' });

        this.showNotification(`${eventName} 카운트다운이 시작되었습니다!`, 'success');
    }

    startCountdownTimer() {
        // Clear existing interval
        if (this.countdownInterval) {
            clearInterval(this.countdownInterval);
        }

        // Update immediately
        this.updateCountdown();

        // Update every second
        this.countdownInterval = setInterval(() => {
            this.updateCountdown();
        }, 1000);
    }

    updateCountdown() {
        if (!this.isActive || !this.targetDate) return;

        const now = new Date().getTime();
        const targetTime = this.targetDate.getTime();
        const difference = targetTime - now;

        if (difference <= 0) {
            this.handleCountdownComplete();
            return;
        }

        // Calculate time units
        const days = Math.floor(difference / (1000 * 60 * 60 * 24));
        const hours = Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((difference % (1000 * 60)) / 1000);

        // Update display with animation
        this.animateNumberChange(this.daysEl, days.toString().padStart(3, '0'));
        this.animateNumberChange(this.hoursEl, hours.toString().padStart(2, '0'));
        this.animateNumberChange(this.minutesEl, minutes.toString().padStart(2, '0'));
        this.animateNumberChange(this.secondsEl, seconds.toString().padStart(2, '0'));

        // Update progress bar
        this.updateProgressBar(difference);
    }

    animateNumberChange(element, newValue) {
        if (element.textContent !== newValue) {
            element.style.transform = 'scale(1.1)';
            element.textContent = newValue;

            setTimeout(() => {
                element.style.transform = 'scale(1)';
            }, 150);
        }
    }

    updateProgressBar(difference) {
        // Calculate progress based on a year timeline
        const oneYear = 365 * 24 * 60 * 60 * 1000;
        const progress = Math.max(0, Math.min(100, ((oneYear - difference) / oneYear) * 100));
        this.progressFill.style.width = `${progress}%`;
    }

    updateDisplay() {
        this.displayEventName.textContent = this.eventName;
        this.displayEventDate.textContent = this.formatDate(this.targetDate);
    }

    formatDate(date) {
        return date.toLocaleDateString('ko-KR', {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        });
    }

    handleCountdownComplete() {
        clearInterval(this.countdownInterval);
        this.isActive = false;

        // Show completion animation
        this.daysEl.textContent = '000';
        this.hoursEl.textContent = '00';
        this.minutesEl.textContent = '00';
        this.secondsEl.textContent = '00';

        this.showNotification(`🎉 ${this.eventName}이(가) 시작되었습니다!`, 'success');
        this.createConfetti();

        // Auto-reset after 5 seconds
        setTimeout(() => {
            this.resetCountdown();
        }, 5000);
    }

    createConfetti() {
        // Simple confetti effect
        const colors = ['#6366f1', '#8b5cf6', '#f59e0b', '#10b981', '#ef4444'];
        const confettiCount = 50;

        for (let i = 0; i < confettiCount; i++) {
            this.createConfettiPiece(colors[Math.floor(Math.random() * colors.length)]);
        }
    }

    createConfettiPiece(color) {
        const confetti = document.createElement('div');
        confetti.style.cssText = `
            position: fixed;
            width: 10px;
            height: 10px;
            background: ${color};
            top: -10px;
            left: ${Math.random() * 100}vw;
            z-index: 1000;
            pointer-events: none;
            border-radius: 50%;
        `;

        document.body.appendChild(confetti);

        const animation = confetti.animate([
            { transform: 'translateY(0) rotateZ(0deg)', opacity: 1 },
            { transform: `translateY(100vh) rotateZ(${Math.random() * 360}deg)`, opacity: 0 }
        ], {
            duration: Math.random() * 3000 + 2000,
            easing: 'cubic-bezier(0.5, 0, 0.5, 1)'
        });

        animation.onfinish = () => confetti.remove();
    }

    resetCountdown() {
        clearInterval(this.countdownInterval);
        this.isActive = false;
        this.countdownSection.classList.remove('active');

        // Reset display
        this.daysEl.textContent = '000';
        this.hoursEl.textContent = '00';
        this.minutesEl.textContent = '00';
        this.secondsEl.textContent = '00';
        this.progressFill.style.width = '0%';

        // Clear inputs
        this.eventNameInput.value = '';
        this.targetDateInput.value = '';
        localStorage.removeItem('countdown-event-name');
        localStorage.removeItem('countdown-target-date');

        this.setDefaultDate();
        this.showNotification('카운트다운이 리셋되었습니다.', 'info');
    }

    startTimezoneUpdates() {
        this.updateTimezones();
        this.timezoneInterval = setInterval(() => {
            this.updateTimezones();
        }, 1000);
    }

    updateTimezones() {
        const now = new Date();

        const timezones = {
            seoul: 'Asia/Seoul',
            tokyo: 'Asia/Tokyo',
            newyork: 'America/New_York',
            london: 'Europe/London',
            paris: 'Europe/Paris',
            sydney: 'Australia/Sydney'
        };

        Object.entries(timezones).forEach(([city, timezone]) => {
            const time = new Date().toLocaleTimeString('ko-KR', {
                timeZone: timezone,
                hour: '2-digit',
                minute: '2-digit',
                second: '2-digit',
                hour12: false
            });

            if (this.timezoneElements[city]) {
                this.timezoneElements[city].textContent = time;
            }
        });
    }

    showNotification(message, type = 'info') {
        // Remove existing notifications
        const existingNotifications = document.querySelectorAll('.notification');
        existingNotifications.forEach(n => n.remove());

        const notification = document.createElement('div');
        notification.className = `notification notification-${type}`;
        notification.textContent = message;

        notification.style.cssText = `
            position: fixed;
            top: 20px;
            right: 20px;
            padding: 1rem 1.5rem;
            border-radius: 12px;
            color: white;
            font-weight: 500;
            z-index: 1000;
            transform: translateX(100%);
            transition: transform 0.3s ease;
            max-width: 400px;
            box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
        `;

        // Set background color based on type
        const colors = {
            success: '#10b981',
            error: '#ef4444',
            info: '#6366f1',
            warning: '#f59e0b'
        };

        notification.style.background = colors[type] || colors.info;

        document.body.appendChild(notification);

        // Animate in
        setTimeout(() => {
            notification.style.transform = 'translateX(0)';
        }, 100);

        // Animate out and remove
        setTimeout(() => {
            notification.style.transform = 'translateX(100%)';
            setTimeout(() => notification.remove(), 300);
        }, 3000);
    }

    // Public methods for external control
    destroy() {
        if (this.countdownInterval) clearInterval(this.countdownInterval);
        if (this.timezoneInterval) clearInterval(this.timezoneInterval);
    }
}

// Initialize the countdown when DOM is loaded
document.addEventListener('DOMContentLoaded', () => {
    const countdown = new GlobalCountdown();

    // Make it globally accessible for debugging
    window.countdown = countdown;

    // Handle page visibility change to pause/resume
    document.addEventListener('visibilitychange', () => {
        if (document.hidden) {
            // Page is hidden, we could pause here if needed
        } else {
            // Page is visible, ensure timezones are updated
            countdown.updateTimezones();
        }
    });

    // Handle page unload
    window.addEventListener('beforeunload', () => {
        countdown.destroy();
    });
});

// Service Worker registration for PWA capabilities (optional)
if ('serviceWorker' in navigator) {
    window.addEventListener('load', () => {
        navigator.serviceWorker.register('/sw.js')
            .then(registration => {
                console.log('SW registered: ', registration);
            })
            .catch(registrationError => {
                console.log('SW registration failed: ', registrationError);
            });
    });
}

