input.content:
import React from 'react';

/**
 * PigAvatar - A cute pig character avatar component
 * Generated from image analysis of test-image.png
 * 
 * Features:
 * - Size variants (small, medium, large)
 * - Hover animations
 * - Accessibility compliant
 * - Click interactions
 */
const PigAvatar = ({ 
  size = 'medium', 
  onClick = null, 
  className = '',
  ariaLabel = 'Pig character avatar'
}) => {
  // Size configurations based on image analysis
  const sizeConfig = {
    small: { width: '32px', height: '32px', strokeWidth: '1.5' },
    medium: { width: '64px', height: '64px', strokeWidth: '2' },
    large: { width: '96px', height: '96px', strokeWidth: '3' }
  };

  const config = sizeConfig[size];

  // Colors extracted from image analysis
  const colors = {
    primary: '#FFA0A0',    // Main pig pink
    secondary: '#FF8080',  // Darker pink
    accent: '#000000',     // Black outlines
    background: '#FFFFFF'  // White background
  };

  const handleClick = () => {
    if (onClick) onClick();
  };

  return (
    <div
      className={`pig-avatar ${className}`}
      onClick={handleClick}
      role={onClick ? "button" : "img"}
      aria-label={ariaLabel}
      tabIndex={onClick ? 0 : -1}
      style={{
        width: config.width,
        height: config.height,
        cursor: onClick ? 'pointer' : 'default',
        borderRadius: '50%',
        display: 'inline-flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: colors.background,
        transition: 'transform 0.2s ease, box-shadow 0.2s ease',
        border: `${config.strokeWidth}px solid ${colors.accent}`,
        // Hover effects
        ':hover': onClick ? {
          transform: 'scale(1.05)',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)'
        } : {}
      }}
    >
      <svg
        width="100%"
        height="100%"
        viewBox="0 0 100 100"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        {/* Pig face circle */}
        <circle
          cx="50"
          cy="50"
          r="40"
          fill={colors.primary}
          stroke={colors.accent}
          strokeWidth={config.strokeWidth}
        />

        {/* Ears */}
        <ellipse
          cx="35"
          cy="25"
          rx="8"
          ry="12"
          fill={colors.primary}
          stroke={colors.accent}
          strokeWidth={config.strokeWidth}
          transform="rotate(-30 35 25)"
        />
        <ellipse
          cx="65"
          cy="25"
          rx="8"
          ry="12"
          fill={colors.primary}
          stroke={colors.accent}
          strokeWidth={config.strokeWidth}
          transform="rotate(30 65 25)"
        />

        {/* Eyes */}
        <circle cx="40" cy="40" r="4" fill={colors.accent} />
        <circle cx="60" cy="40" r="4" fill={colors.accent} />

        {/* Snout */}
        <ellipse
          cx="50"
          cy="60"
          rx="12"
          ry="8"
          fill={colors.secondary}
          stroke={colors.accent}
          strokeWidth={config.strokeWidth}
        />

        {/* Nostrils */}
        <circle cx="46" cy="60" r="2" fill={colors.accent} />
        <circle cx="54" cy="60" r="2" fill={colors.accent} />
      </svg>
    </div>
  );
};

// CSS-in-JS styles for hover effects (if using styled-components)
const StyledPigAvatar = {
  transition: 'all 0.2s ease',
  '&:hover': {
    transform: 'scale(1.05)',
    filter: 'drop-shadow(0 4px 8px rgba(0, 0, 0, 0.2))'
  }
};

export default PigAvatar;

// Usage examples:
/*
// Basic usage
<PigAvatar />

// Different sizes
<PigAvatar size="small" />
<PigAvatar size="large" />

// Interactive with click handler
<PigAvatar 
  size="medium" 
  onClick={() => console.log('Pig clicked!')}
  ariaLabel="Click to select pig avatar"
/>

// Custom styling
<PigAvatar 
  className="my-custom-pig"
  size="large"
/>
*/

