        <circle cx="40" cy="40" r="4" fill={colors.accent} />
        <circle cx="60" cy="40" r="4" fill={colors.accent} />

        {/* Snout */}
        <ellipse
          cx="50"
          cy="60"
          rx="12"
          ry="8"
          fill={colors.secondary}
          stroke={colors.accent}
          strokeWidth={config.strokeWidth}
        />

        {/* Nostrils */}
        <circle cx="46" cy="60" r="2" fill={colors.accent} />
        <circle cx="54" cy="60" r="2" fill={colors.accent} />
      </svg>
    </div>
  );
};

// CSS-in-JS styles for hover effects (if using styled-components)
const StyledPigAvatar = {
  transition: 'all 0.2s ease',
  '&:hover': {
    transform: 'scale(1.05)',
    filter: 'drop-shadow(0 4px 8px rgba(0, 0, 0, 0.2))'
  }
};

export default PigAvatar;

// Usage examples:
/*
// Basic usage
<PigAvatar />

// Different sizes
