<PigAvatar size="large" />

// Interactive with click handler
<PigAvatar 
  size="medium" 
  onClick={() => console.log('Pig clicked!')}
  ariaLabel="Click to select pig avatar"
/>

// Custom styling
<PigAvatar 
  className="my-custom-pig"
  size="large"
/>
