import { defineConfig } from 'vite'
import react from '@vitejs/plugin-react'

export default defineConfig({
  plugins: [react()],
  server: {
    host: '0.0.0.0',
    port: 3000,
  },
  define: {
    global: 'globalThis',
  },
  resolve: {
    alias: {
      '@': '/src',
    },
  },
})

{
  "compilerOptions": {
    "composite": true,
    "skipLibCheck": true,
    "module": "ESNext",
    "moduleResolution": "bundler",
    "allowSyntheticDefaultImports": true
  },
  "include": ["vite.config.ts"]
}

import React from 'react'
import ReactDOM from 'react-dom/client'
import { ChakraProvider } from '@chakra-ui/react'
import { QueryClient, QueryClientProvider } from '@tanstack/react-query'
import App from './App.tsx'

const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      staleTime: 5 * 60 * 1000, // 5분
      retry: 1,
    },
  },
})

ReactDOM.createRoot(document.getElementById('root')!).render(
  <React.StrictMode>
    <ChakraProvider>
      <QueryClientProvider client={queryClient}>
        <App />
      </QueryClientProvider>
    </ChakraProvider>
  </React.StrictMode>,
)

import {
  Box,
  Button,
  VStack,
  Text,
  useToast,
  Input,
  Textarea,
  HStack,
  Spinner,
} from '@chakra-ui/react'
import { useDropzone } from 'react-dropzone'
import { Upload, FileText } from 'lucide-react'
import { useState } from 'react'
import { uploadDocument, generateSummary } from '../services/api'
import { SummaryData } from '../App'

interface DocumentUploadProps {
  onSummaryGenerated: (data: SummaryData) => void
}

const DocumentUpload = ({ onSummaryGenerated }: DocumentUploadProps) => {
  const [files, setFiles] = useState<File[]>([])
  const [query, setQuery] = useState('이 문서들의 주요 내용을 요약해주세요.')
  const [isUploading, setIsUploading] = useState(false)
  const [isSummarizing, setIsSummarizing] = useState(false)
  const toast = useToast()

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: {
      'application/pdf': ['.pdf'],
      'text/plain': ['.txt'],
      'text/markdown': ['.md'],
    },
    maxFiles: 5,
    onDrop: (acceptedFiles) => {
      setFiles(prev => [...prev, ...acceptedFiles])
    },
  })

  const handleUpload = async () => {
    if (files.length === 0) {
      toast({
        title: '파일을 선택해주세요',
        status: 'warning',
        duration: 3000,
      })
      return
    }

    setIsUploading(true)

    try {
      for (const file of files) {
        await uploadDocument(file)
      }

      toast({
        title: '업로드 완료',
        description: `${files.length}개 파일이 성공적으로 업로드되었습니다.`,
        status: 'success',
        duration: 3000,
      })
    } catch (error) {
      toast({
        title: '업로드 실패',
        description: error instanceof Error ? error.message : '알 수 없는 오류',
        status: 'error',
        duration: 5000,
      })
    } finally {
      setIsUploading(false)
    }
  }

  const handleSummarize = async () => {
    setIsSummarizing(true)

    try {
      const result = await generateSummary(query)
      onSummaryGenerated(result)

      toast({
        title: '요약 완료',
        description: '문서 요약이 생성되었습니다.',
        status: 'success',
        duration: 3000,
      })
    } catch (error) {
      toast({
        title: '요약 실패',
        description: error instanceof Error ? error.message : '알 수 없는 오류',
        status: 'error',
        duration: 5000,
      })
    } finally {
      setIsSummarizing(false)
    }
  }

  return (
    <VStack spacing={6} align="stretch">
      {/* 파일 업로드 영역 */}
      <Box
        {...getRootProps()}
        p={8}
        border="2px dashed"
        borderColor={isDragActive ? 'blue.300' : 'gray.300'}
        borderRadius="lg"
        bg={isDragActive ? 'blue.50' : 'gray.50'}
        cursor="pointer"
        transition="all 0.2s"
        _hover={{ borderColor: 'blue.400', bg: 'blue.50' }}
      >
        <input {...getInputProps()} />
        <VStack spacing={4}>
          <Upload size={40} color={isDragActive ? '#3182CE' : '#A0ADB8'} />
          <VStack spacing={2}>
            <Text fontSize="lg" fontWeight="medium">
              {isDragActive ? '파일을 놓아주세요' : '파일을 드래그하거나 클릭하여 업로드'}
            </Text>
            <Text fontSize="sm" color="gray.600">
              PDF, TXT, MD 파일 지원 (최대 5개)
            </Text>
          </VStack>
        </VStack>
      </Box>

      {/* 선택된 파일 목록 */}
      {files.length > 0 && (
        <Box p={4} bg="gray.50" borderRadius="md">
          <Text fontWeight="medium" mb={3}>선택된 파일:</Text>
          <VStack align="start" spacing={2}>
            {files.map((file, index) => (
              <HStack key={index}>
                <FileText size={16} />
                <Text fontSize="sm">{file.name}</Text>
                <Text fontSize="xs" color="gray.500">
                  ({(file.size / 1024 / 1024).toFixed(2)} MB)
                </Text>
              </HStack>
            ))}
          </VStack>
        </Box>
      )}

      {/* 업로드 버튼 */}
      <Button
        onClick={handleUpload}
        isLoading={isUploading}
        loadingText="업로드 중..."
        colorScheme="blue"
        size="lg"
        disabled={files.length === 0}
      >
        문서 업로드
      </Button>

      {/* 요약 쿼리 입력 */}
      <Box>
        <Text fontWeight="medium" mb={2}>요약 요청:</Text>
        <Textarea
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="어떤 내용으로 요약하고 싶으신가요?"
          rows={3}
        />
      </Box>

      {/* 요약 생성 버튼 */}
      <Button
        onClick={handleSummarize}
        isLoading={isSummarizing}
        loadingText="요약 생성 중..."
        colorScheme="green"
        size="lg"
        leftIcon={isSummarizing ? <Spinner size="sm" /> : undefined}
      >
        요약 생성
      </Button>
    </VStack>
  )
}

export default DocumentUpload

import axios from 'axios'
import { SummaryData } from '../App'

const API_BASE_URL = import.meta.env.VITE_API_URL || 'http://localhost:8000'

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
  },
})

// 요청/응답 인터셉터
api.interceptors.request.use(
  (config) => {
    console.log(`API Request: ${config.method?.toUpperCase()} ${config.url}`)
    return config
  },
  (error) => {
    console.error('API Request Error:', error)
    return Promise.reject(error)
  }
)

api.interceptors.response.use(
  (response) => {
    console.log(`API Response: ${response.status} ${response.config.url}`)
    return response
  },
  (error) => {
    console.error('API Response Error:', error.response?.data || error.message)
    return Promise.reject(error)
  }
)

// 타입 정의
export interface DocumentUploadResponse {
  filename: string
  file_size: number
  chunks_count: number
  message: string
}

export interface SummarizeRequest {
  query?: string
  max_tokens?: number
}

// API 함수들
export const uploadDocument = async (file: File): Promise<DocumentUploadResponse> => {
  const formData = new FormData()
  formData.append('file', file)

  try {
    const response = await api.post('/api/documents/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
    return response.data
  } catch (error) {
    if (axios.isAxiosError(error)) {
      throw new Error(error.response?.data?.detail || '파일 업로드에 실패했습니다.')
    }
    throw error
  }
}

export const generateSummary = async (query: string = '이 문서들의 주요 내용을 요약해주세요.'): Promise<SummaryData> => {
  try {
    const response = await api.post('/api/summarize/generate', {
      query,
      max_tokens: 1000,
    })
    return response.data
  } catch (error) {
    if (axios.isAxiosError(error)) {
      throw new Error(error.response?.data?.detail || '요약 생성에 실패했습니다.')
    }
    throw error
  }
}

export const searchDocuments = async (query: string, k: number = 5) => {
  try {
    const response = await api.get('/api/documents/search', {
      params: { query, k }
    })
    return response.data
  } catch (error) {
    if (axios.isAxiosError(error)) {
      throw new Error(error.response?.data?.detail || '문서 검색에 실패했습니다.')
    }
    throw error
  }
}

export const healthCheck = async () => {
  try {
    const response = await api.get('/health')
    return response.data
  } catch (error) {
    if (axios.isAxiosError(error)) {
      throw new Error('서버 연결에 실패했습니다.')
    }
    throw error
  }
}

