javascriptconst cron = require('node-cron');

// 매일 오후 6시에 내일 경기 리마인더
cron.schedule('0 18 * * *', async () => {
  const tomorrow = new Date();
  tomorrow.setDate(tomorrow.getDate() + 1);

  const tomorrowGames = await Game.find({
    date: tomorrow.toISOString().split('T')[0],
    status: { $in: ['recruiting', 'full'] }
  });

  for (const game of tomorrowGames) {
    const reminderContent = `⏰ 내일 경기 리마인더
🏸 ${game.level} 경기
📅 ${game.date} ${game.time}
🏟️ ${game.court.name}

참가자분들 준비 잘 하세요! 🏸
늦지 마시고, 라켓과 운동복 챙기세요~

#리마인더 #배드민턴`;

    await BandAPI.createPost(null, 0, reminderContent, true);
  }
});

// 주간 랭킹 자동 발표 (매주 일요일 저녁 8시)
cron.schedule('0 20 * * 0', async () => {
  const weeklyRanking = await Member.find()
    .sort({ 'stats.wins': -1, 'stats.losses': 1 })
    .limit(10);

  const rankingContent = `🏆 주간 랭킹 TOP 10 🏆

${weeklyRanking.map((member, index) => 
  `${index + 1}위: ${member.name} (${member.stats.wins}승 ${member.stats.losses}패)`
).join('\n')}

다음 주도 열심히! 💪
#주간랭킹 #배드민턴`;

  await BandAPI.createPost(null, 0, rankingContent);
});
