javascript// Socket.IO를 활용한 실시간 참가자 업데이트
const io = require('socket.io')(server);

io.on('connection', (socket) => {
  socket.on('joinGame', async (gameId, userInfo) => {
    try {
      const game = await Game.findById(gameId);

      if (game.participants.length < game.maxPlayers) {
        game.participants.push(userInfo);
        await game.save();

        // 모든 클라이언트에 업데이트 전송
        io.emit('gameUpdated', game);

        // 밴드에 참가자 업데이트 포스트
        if (game.participants.length % 2 === 0) { // 2명씩 늘어날 때마다
          await updateParticipants(gameId, game.participants);
        }

        socket.emit('joinSuccess', '참가 신청이 완료되었습니다!');
      } else {
        socket.emit('joinFailed', '이미 정원이 찼습니다.');
      }
    } catch (error) {
      socket.emit('joinFailed', '참가 신청 중 오류가 발생했습니다.');
    }
  });
});
