#include "StdAfx.h"
#include "C:\FIDES_VS2019\FIDES_WARE\APP_HEADER-Ver3x\GUI\UTILITY\interface_gui_3v.h"

#include "CSTD_Password.h"
#include "Dlg_User_Level.h"
#include "CClass_Macro.h"

extern HINSTANCE	mSys_Instance;

int CSTD_Password_Ex_CALL(CInterface_GUI_Ex **ppt)
{
	CSTD_Password *pt = new CSTD_Password;

	*ppt = pt;
	return 1;
}


//-----------------------------------------------------------------------------------
int  CSTD_Password::Interface_Parameter(const int& module_id,
										const POINT& st,
										const POINT& et,
										const CStringArray& para_list)
{

	//--------------------------------------------------
	CGX__UTILITY_CTRL cx_utility_ctrl;

	int i;
	int limit = para_list.GetSize();

	for(i=0;i<limit;i++)
	{
		// 00 : User-ID
		if(i == 0)
		{
			cx_utility_ctrl->Get__CHANNEL_NAME(para_list[i],mCH_User_ID);  
			continue;
		}

		// 01 : User-Level
		if(i == 1)
		{
			cx_utility_ctrl->Get__CHANNEL_NAME(para_list[i],mCH_User_Level);  
			continue;
		}
	}
	//--------------------------------------------------

	pST = st;

