#include "StdAfx.h"
#include "resource.h"

#include "CSTD__ALARM_PAGE.h"
#include "Dlg__Alarm_Clear_List.h"
#include "Dlg__Alarm_User_Msg.h"
#include "Dlg_Image_Open.h"


extern HINSTANCE mSys_Instance;

#define ID_USEREDIT1	103
#define ID_USEREDIT2	104
#define ID_USEREDIT3	105

int CSTD__ALARM_PAGE__CALL(CInterface_GUI_Ex **ppt)
{
	CSTD__ALARM_PAGE *pt = new CSTD__ALARM_PAGE;

	*ppt = pt;
	return 1;
}


//-----------------------------------------------------------------------------------
CSTD__ALARM_PAGE::CSTD__ALARM_PAGE()
{
	m_hEdit1 = NULL;
	m_hEdit2 = NULL;
	m_hEdit3 = NULL;

	InitializeCriticalSection(&mCS_LOCK);
	iDB_LOCK = -1;


	//----------------------------------------------
	int i;

	mI_Draw    = New_DRAW();
	mI_Utility = New_UTILITY();

	mI_TITLE_TIME    = New_BUTTON();
	mI_TITLE_MODULE  = New_BUTTON();
	mI_TITLE_ID      = New_BUTTON();
	mI_TITLE_MESSAGE = New_BUTTON();

	for(i=0;i<ROW_LIMIT+1;i++)
	{
		mI_TIME[i]    = New_BUTTON();
		mI_MODULE[i]  = New_BUTTON();
		mI_ID[i]      = New_BUTTON();
		mI_MESSAGE[i] = New_BUTTON();
	}

	mI_VScrollbar = New_VSCROLLBAR();


	//----------------------------------------------
	// ACTION - KEY

	// Sub-Back -----
	{
		mI_SUB_BACK = New_BMP();

		mI_SUB_BACK->BackgroundRestoreDisable();
		mI_SUB_BACK->MaskDisable();
		mI_SUB_BACK->Load(mSys_Instance,IDB_SUB_BACK,NULL);
	}

	// User MSG CLEAR -----
	{
		mI_USER_MSG_ON  = New_BMP();
		mI_USER_MSG_OFF = New_BMP();
		mI_USER_MSG_DISABLE = New_BMP();

		mI_USER_MSG_ON->BackgroundRestoreDisable();
		mI_USER_MSG_OFF->BackgroundRestoreDisable();
		mI_USER_MSG_DISABLE->BackgroundRestoreDisable();

		mI_USER_MSG_ON->MaskDisable();

