#include "StdAfx.h"
#include "CObj__OPR_AUTO_MODE.h"

CObj__OPR_AUTO_MODE::CObj__OPR_AUTO_MODE()
{
}
CObj__OPR_AUTO_MODE::~CObj__OPR_AUTO_MODE()
{

}

//-------------------------------------------------------------------------
int CObj__OPR_AUTO_MODE::DEFINE__OBJECT_MODE(obj,l_mode)
{
	sObject_Name = obj;

	l_mode.Add("TEST_INIT_MODE");	

	l_mode.Add(SCH_CMMD_CJOB_START);
	l_mode.Add(SCH_CMMD_CJOB_DISCHARGE);
	l_mode.Add(SCH_CMMD_CJOB_ALLDISCHARGE);	
	l_mode.Add(SCH_CMMD_CJOB_MOVE);
	l_mode.Add(SCH_CMMD_WITHDRAW_START);
	l_mode.Add(SCH_CMMD_JOB_ABORT);
	l_mode.Add(SCH_CMMD_JOB_DELETE);
	l_mode.Add(SCH_CMMD_JOB_RESUME);
	l_mode.Add(SCH_CMMD_JOB_PAUSE);
	l_mode.Add(SCH_CMMD_JOB_CLEAR);

	l_mode.Add("SET_AUTO");



	return SEQ_AVAILABLE;
}

int CObj__OPR_AUTO_MODE::DEFINE__VERSION_HISTORY(version)
{
	return SEQ_AVAILABLE;
}

#define  MON_ID__JOB_MANAGER			1

#define  MON_ID__FRB_TRANSFER_MONITOR	11
#define  MON_ID__WTR_TRANSFER_MONITOR	12
#define  MON_ID__FIMS_MONITOR			13
#define  MON_ID__CHAMBER_COOLING		14

#define  MON_ID__RCMD_JOBCTRL_MONITOR	20


int CObj__OPR_AUTO_MODE::DEFINE__VARIABLE(p_variable)
{
	DECLARE__VIRTUAL_VARIABLE;

	//-------------------------------------------------------------------------------
	CString szCH_Name;
//	int		i;


	szCH_Name = "SCH.iFOUP_FIMS_COUNT";
	VIRTUAL__ADD_ANALOG(szCH_Name, "SLOT", 0, 0, 100);
	LINK__VAR_ANALOG_CTRL(aCH__iFOUP_FIMS_COUNT,szCH_Name);

	szCH_Name = "SCH.iFOUP_STK_COUNT";
	VIRTUAL__ADD_ANALOG(szCH_Name, "SLOT", 0, 0, 100);
	LINK__VAR_ANALOG_CTRL(aCH__iFOUP_STK_COUNT,szCH_Name);

	szCH_Name = "SCH.iMAX_FOUP_COUNT";
	VIRTUAL__ADD_ANALOG(szCH_Name, "SLOT", 0, 0, 100);
	LINK__VAR_ANALOG_CTRL(aCH__iMAX_FOUP_COUNT,szCH_Name);


	p_variable->Add__MONITORING_PROC(1.0, MON_ID__JOB_MANAGER);
	p_variable->Add__MONITORING_PROC(1.0, MON_ID__FRB_TRANSFER_MONITOR);
	p_variable->Add__MONITORING_PROC(1.0, MON_ID__WTR_TRANSFER_MONITOR);
	p_variable->Add__MONITORING_PROC(1.0, MON_ID__FIMS_MONITOR);	
	p_variable->Add__MONITORING_PROC(1.0, MON_ID__CHAMBER_COOLING);

//  	p_variable->Add__MONITORING_PROC(1.0, MON_ID__MAIN_RECIPE);

