// ✅ 올바른 메모리 관리 패턴
class CSTD__ALARM_PAGE 
{
private:
    std::unique_ptr<DRAW> mI_Draw;       // 스마트 포인터 사용
    std::vector<std::unique_ptr<BUTTON>> mI_Buttons;  // 컨테이너 사용

public:
