.calculator-keypad {
  flex: 1;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: var(--space-3);
  grid-template-areas:
    "clear clear percent divide"
    "seven eight nine multiply"
    "four five six subtract"
    "one two three add"
    "zero zero decimal equals";
}

/* 특별 버튼 배치 */
.calc-button--zero {
  grid-area: zero;
}

.calc-button--clear {
  grid-area: clear;
}

.calc-button--percent {
  grid-area: percent;
}

.calc-button--equals {
  grid-area: equals;
}
