// components/AccessibilityAnnouncement.tsx
import React, { useEffect } from 'react';
import { AccessibilityInfo, Platform } from 'react-native';

interface AccessibilityAnnouncementProps {
  message: string;
  priority?: 'low' | 'medium' | 'high';
}

export const AccessibilityAnnouncement: React.FC<AccessibilityAnnouncementProps> = ({
  message,
  priority = 'medium'
}) => {
  useEffect(() => {
    if (message) {
      // iOS VoiceOver
      if (Platform.OS === 'ios') {
        AccessibilityInfo.announceForAccessibility(message);
      }
      // Android TalkBack
      else if (Platform.OS === 'android') {
        AccessibilityInfo.setAccessibilityFocus(
          AccessibilityInfo.announceForAccessibility(message)
        );
      }
    }
  }, [message]);

  return null;
};

// 사용 예시
const Calculator = () => {
  const [announcement, setAnnouncement] = useState('');

  const handleCalculation = (result: string) => {
    setAnnouncement(`계산 결과: ${result}`);
  };

  return (
    <View>
      <AccessibilityAnnouncement message={announcement} />
      {/* 기타 컴포넌트들 */}
    </View>
  );
};
