```tsx
// hooks/useAccessibleFontSize.ts
import { useState, useEffect } from 'react';
import { PixelRatio, Platform } from 'react-native';

export const useAccessibleFontSize = () => {
  const [fontScale, setFontScale] = useState(1);

  useEffect(() => {
    const getCurrentFontScale = () => {
      const scale = PixelRatio.getFontScale();
      setFontScale(Math.min(scale, 2)); // 최대 200%까지만 지원
    };

    getCurrentFontScale();

    // iOS에서 폰트 크기 변경 감지
    if (Platform.OS === 'ios') {
      const subscription = PixelRatio.addListener?.(getCurrentFontScale);
      return () => subscription?.remove?.();
    }
  }, []);

  const getScaledSize = (baseSize: number) => {
    return Math.ceil(baseSize * fontScale);
  };

  return {
    fontScale,
    getScaledSize,
    isLargeFont: fontScale > 1.3,
  };
};

// 컴포넌트에서 사용
const CalculatorButton = () => {
  const { getScaledSize, isLargeFont } = useAccessibleFontSize();

  return (
    <TouchableOpacity
      style={[
        styles.button,
        isLargeFont && styles.buttonLarge
      ]}
    >
      <Text style={{ fontSize: getScaledSize(18) }}>
        1
      </Text>
    </TouchableOpacity>
  );
};
```

