// hooks/useHighContrast.ts
import { useState, useEffect } from 'react';
import { AccessibilityInfo, Platform } from 'react-native';

export const useHighContrast = () => {
  const [isHighContrastEnabled, setIsHighContrastEnabled] = useState(false);

  useEffect(() => {
    const checkHighContrast = async () => {
      try {
        if (Platform.OS === 'ios') {
          // iOS는 별도 API가 없으므로 설정에서 확인
          const isEnabled = await AccessibilityInfo.isReduceTransparencyEnabled();
          setIsHighContrastEnabled(isEnabled);
        } else if (Platform.OS === 'android') {
          // Android의 고대비 설정 확인 (API 레벨에 따라 다름)
          // 실제 구현에서는 네이티브 모듈이 필요할 수 있음
        }
      } catch (error) {
        console.warn('High contrast detection failed:', error);
      }
    };

    checkHighContrast();
  }, []);

  return isHighContrastEnabled;
};

// 테마에서 활용
export const useTheme = () => {
  const isHighContrast = useHighContrast();
  const { settings } = useSettings();

  const getTheme = () => {
    if (isHighContrast || settings.theme === 'high-contrast') {
      return highContrastTheme;
    }

    if (settings.theme === 'dark') {
      return darkTheme;
    }

    return lightTheme;
  };

  return getTheme();
};
```

