// components/OptimizedCalculatorButton.tsx
import React, { memo, useCallback } from 'react';
import { TouchableOpacity, Text } from 'react-native';

interface ButtonProps {
  value: string;
  label: string;
  onPress: (value: string) => void;
  isPressed: boolean;
}

export const OptimizedCalculatorButton = memo<ButtonProps>(({
  value,
  label,
  onPress,
  isPressed
}) => {
  const handlePress = useCallback(() => {
    onPress(value);
  }, [value, onPress]);

  return (
    <TouchableOpacity
      onPress={handlePress}
      style={[
        styles.button,
        isPressed && styles.buttonPressed
      ]}
    >
      <Text style={styles.buttonText}>
        {label}
      </Text>
    </TouchableOpacity>
  );
}, (prevProps, nextProps) => {
  // 필요한 props만 비교하여 리렌더링 최소화
  return (
    prevProps.value === nextProps.value &&
    prevProps.label === nextProps.label &&
    prevProps.isPressed === nextProps.isPressed
  );
});
