```tsx
// hooks/useOptimizedAnimation.ts
import { useSharedValue, useAnimatedStyle, withSpring } from 'react-native-reanimated';
import { useSettings } from './useSettings';

export const useOptimizedAnimation = () => {
  const { settings } = useSettings();
  const scale = useSharedValue(1);

  const animatedStyle = useAnimatedStyle(() => {
    return {
      transform: [
        {
          scale: withSpring(scale.value, {
            damping: 15,
            stiffness: 200,
            // 애니메이션 줄이기 설정 반영
            duration: settings.reduceMotion ? 1 : undefined,
          }),
        },
      ],
    };
  }, [settings.reduceMotion]);

  const triggerPressAnimation = useCallback(() => {
    if (settings.reduceMotion) return;

    scale.value = 0.95;
    setTimeout(() => {
      scale.value = 1;
    }, 100);
  }, [settings.reduceMotion]);

  return {
    animatedStyle,
    triggerPressAnimation,
  };
};
```

### 3. 메모리 최적화

```tsx
// hooks/useCalculatorHistory.ts
import { useState, useCallback, useMemo } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';

interface HistoryItem {
  id: string;
  expression: string;
  result: string;
  timestamp: number;
}

const MAX_HISTORY_ITEMS = 100;
const STORAGE_KEY = 'calculator_history';

export const useCalculatorHistory = () => {
  const [history, setHistory] = useState<HistoryItem[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  // 메모이제이션된 최근 항목
  const recentHistory = useMemo(() => {
    return history.slice(0, 20); // 최근 20개만 표시
  }, [history]);

  const addHistoryItem = useCallback(async (expression: string, result: string) => {
    const newItem: HistoryItem = {
      id: `${Date.now()}-${Math.random()}`,
      expression,
      result,
      timestamp: Date.now(),
    };

    setHistory(prev => {
      const updated = [newItem, ...prev].slice(0, MAX_HISTORY_ITEMS);

      // 백그라운드에서 저장 (UI 블로킹 방지)
      AsyncStorage.setItem(STORAGE_KEY, JSON.stringify(updated));

      return updated;
    });
  }, []);

  const clearHistory = useCallback(async () => {
    setHistory([]);
    await AsyncStorage.removeItem(STORAGE_KEY);
  }, []);

  const loadHistory = useCallback(async () => {
    if (isLoading) return;

    setIsLoading(true);
    try {
      const stored = await AsyncStorage.getItem(STORAGE_KEY);
      if (stored) {
        const parsed = JSON.parse(stored);
        setHistory(parsed);
      }
    } catch (error) {
      console.warn('Failed to load history:', error);
    } finally {
      setIsLoading(false);
    }
  }, [isLoading]);

  return {
    history: recentHistory,
    addHistoryItem,
    clearHistory,
    loadHistory,
    isLoading,
  };
};
```

