      expect(getByDisplayValue('5')).toBeTruthy();
    });
  });

  test('연속 계산이 올바르게 작동한다', async () => {
    const { getByLabelText, getByDisplayValue } = render(<Calculator />);

    // 10 - 5 + 2 = 계산
    fireEvent.press(getByLabelText('숫자 1'));
    fireEvent.press(getByLabelText('숫자 0'));
    fireEvent.press(getByLabelText('빼기'));
    fireEvent.press(getByLabelText('숫자 5'));
    fireEvent.press(getByLabelText('더하기'));
    fireEvent.press(getByLabelText('숫자 2'));
