```tsx
// components/CalculatorButton.tsx
import React from 'react';
import { 
  TouchableOpacity, 
  Text, 
  StyleSheet, 
  GestureResponderEvent,
  AccessibilityRole 
} from 'react-native';
import { useTheme } from '../hooks/useTheme';

interface CalculatorButtonProps {
  button: CalculatorButton;
  onPress: (value: string) => void;
  isPressed?: boolean;
  size?: 'small' | 'medium' | 'large';
}

export const CalculatorButton: React.FC<CalculatorButtonProps> = ({
  button,
  onPress,
  isPressed = false,
  size = 'medium'
}) => {
  const theme = useTheme();

  const handlePress = (event: GestureResponderEvent) => {
    onPress(button.value.toString());
  };

  const getButtonStyle = () => {
    const baseStyle = [
      styles.button,
      styles[`button-${size}`],
      { backgroundColor: theme.colors[`btn-${button.type}`] }
    ];

    if (isPressed) {
      baseStyle.push(styles.buttonPressed);
    }

    return baseStyle;
  };

  return (
    <TouchableOpacity
      style={getButtonStyle()}
      onPress={handlePress}
      accessible={true}
      accessibilityRole={'button' as AccessibilityRole}
      accessibilityLabel={button.ariaLabel}
      accessibilityHint={`계산기 ${button.ariaLabel} 버튼`}
      accessibilityState={{
        pressed: isPressed
      }}
      activeOpacity={0.7}
      hitSlop={{ top: 8, bottom: 8, left: 8, right: 8 }}
    >
      <Text 
        style={[
          styles.buttonText, 
          styles[`buttonText-${button.type}`],
          { color: theme.colors[`btn-${button.type}-text`] }
        ]}
      >
        {button.label}
      </Text>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  button: {
    borderRadius: 8,
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 3.84,
    elevation: 5,
  },
  'button-small': {
    minHeight: 44,
    minWidth: 44,
  },
  'button-medium': {
    minHeight: 56,
    minWidth: 56,
  },
  'button-large': {
    minHeight: 72,
    minWidth: 72,
  },
  buttonPressed: {
    transform: [{ scale: 0.95 }],
    shadowOpacity: 0.05,
  },
  buttonText: {
    fontFamily: 'SF Pro Display',
    fontWeight: '500',
    textAlign: 'center',
  },
  'buttonText-number': {
    fontSize: 18,
  },
  'buttonText-operator': {
    fontSize: 20,
    fontWeight: '600',
  },
  'buttonText-equals': {
    fontSize: 24,
    fontWeight: '700',
  },
  'buttonText-function': {
    fontSize: 16,
  },
});
