```tsx
// components/CalculatorDisplay.tsx
import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { useTheme } from '../hooks/useTheme';
import Animated, { 
  FadeIn, 
  FadeOut,
  useAnimatedStyle,
  withSpring 
} from 'react-native-reanimated';

interface CalculatorDisplayProps {
  display: CalculatorDisplay;
  size?: 'compact' | 'normal' | 'large';
}

export const CalculatorDisplay: React.FC<CalculatorDisplayProps> = ({
  display,
  size = 'normal'
}) => {
  const theme = useTheme();

  const animatedStyle = useAnimatedStyle(() => {
    return {
      transform: [
        {
          scale: withSpring(display.isCalculating ? 1.02 : 1, {
            damping: 15,
            stiffness: 200,
          }),
        },
      ],
    };
  });

  const getResultFontSize = () => {
    const length = display.result.length;
    if (length > 12) return theme.fontSize.large;
    if (length > 8) return theme.fontSize.xlarge;
    return 48; // 기본 큰 크기
  };

  return (
    <Animated.View 
      style={[
        styles.container, 
        styles[`container-${size}`],
        { backgroundColor: theme.colors.primary }
      ]}
      entering={FadeIn}
      exiting={FadeOut}
    >
      {/* 수식 표시 영역 */}
      <View style={styles.expressionContainer}>
        <Text 
          style={[
            styles.expression,
            { color: theme.colors.textSecondary }
          ]}
          numberOfLines={2}
          ellipsizeMode="head"
          accessible={true}
          accessibilityLabel={`계산 수식: ${display.expression}`}
        >
          {display.expression}
        </Text>
      </View>

      {/* 결과 표시 영역 */}
      <Animated.View style={animatedStyle}>
        <Text
          style={[
            styles.result,
            {
              color: display.isError ? theme.colors.error : theme.colors.textPrimary,
              fontSize: getResultFontSize(),
            }
          ]}
          numberOfLines={1}
          adjustsFontSizeToFit={true}
          minimumFontScale={0.5}
          accessible={true}
          accessibilityLabel={`계산 결과: ${display.result}`}
          accessibilityLiveRegion="polite"
        >
          {display.result}
        </Text>
      </Animated.View>

      {/* 계산 중 표시 */}
      {display.isCalculating && (
        <Animated.View
          style={styles.loadingIndicator}
          entering={FadeIn}
          exiting={FadeOut}
        >
          <View style={styles.spinner} />
        </Animated.View>
      )}
    </Animated.View>
  );
};

const styles = StyleSheet.create({
  container: {
    borderRadius: 12,
    padding: 20,
    minHeight: 120,
    justifyContent: 'flex-end',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.08,
    shadowRadius: 2,
    elevation: 3,
  },
  'container-compact': {
    minHeight: 80,
    padding: 12,
  },
  'container-normal': {
    minHeight: 120,
    padding: 20,
  },
  'container-large': {
    minHeight: 160,
    padding: 24,
  },
  expressionContainer: {
    marginBottom: 8,
    minHeight: 20,
  },
  expression: {
    fontFamily: 'SF Mono',
    fontSize: 16,
    textAlign: 'right',
    opacity: 0.8,
  },
  result: {
    fontFamily: 'SF Mono',
    fontWeight: '500',
    textAlign: 'right',
    lineHeight: 56,
  },
  loadingIndicator: {
    position: 'absolute',
    top: 8,
    right: 8,
  },
  spinner: {
    width: 16,
    height: 16,
    borderRadius: 8,
    borderWidth: 2,
    borderColor: '#1976D2',
    borderTopColor: 'transparent',
  },
});
