  },
  seeAllText: {
    fontSize: 14,
    color: theme.colors.primary,
  },
  statsGrid: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginBottom: theme.spacing.lg,
  },
  statItem: {
    alignItems: 'center',
  },
  statNumber: {
    fontSize: 24,
    fontWeight: 'bold',
    color: theme.colors.primary,
  },
  statLabel: {
    fontSize: 12,
    color: theme.colors.text,
    marginTop: theme.spacing.xs,
  },
  rankingContainer: {
    marginTop: theme.spacing.md,
  },
  rankingLabel: {
    fontSize: 14,
    fontWeight: '600',
    color: theme.colors.text,
    marginBottom: theme.spacing.sm,
  },
  progressBar: {
    height: 8,
    borderRadius: 4,
    marginBottom: theme.spacing.sm,
  },
  progressText: {
    fontSize: 12,
    color: theme.colors.text,
    textAlign: 'center',
  },
  activityCard: {
    margin: theme.spacing.lg,
    marginTop: 0,
