import React from 'react';
import { View, StyleSheet, ImageBackground, Image } from 'react-native';
import { Text, Button, Surface } from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';
import { theme } from '../../utils/theme';

const WelcomeScreen = ({ navigation }) => {
  return (
    <ImageBackground
      source={require('../../../assets/badminton-bg.jpg')}
      style={styles.background}
      blurRadius={3}
    >
      <SafeAreaView style={styles.container}>
        <View style={styles.content}>
          {/* 로고 영역 */}
          <View style={styles.logoContainer}>
            <Surface style={styles.logoSurface} elevation={4}>
              <Image
                source={require('../../../assets/logo.png')}
                style={styles.logo}
                resizeMode="contain"
              />
            </Surface>
            <Text style={styles.title}>동배즐</Text>
            <Text style={styles.subtitle}>함께하는 배드민턴의 즐거움</Text>
          </View>

          {/* 설명 텍스트 */}
          <View style={styles.descriptionContainer}>
            <Text style={styles.description}>
              배드민턴을 사랑하는 사람들이 모이는 곳
            </Text>
            <Text style={styles.subDescription}>
              클럽 가입부터 경기까지, 모든 것이 한 곳에
            </Text>
          </View>

          {/* 버튼 영역 */}
          <View style={styles.buttonContainer}>
            <Button
              mode="contained"
              onPress={() => navigation.navigate('Login')}
              style={styles.loginButton}
              labelStyle={styles.loginButtonText}
              contentStyle={styles.buttonContent}
            >
              로그인
            </Button>

            <Button
              mode="outlined"
              onPress={() => navigation.navigate('Signup')}
              style={styles.signupButton}
              labelStyle={styles.signupButtonText}
              contentStyle={styles.buttonContent}
            >
              회원가입
            </Button>

            <Button
              mode="text"
              onPress={() => navigation.navigate('ForgotPassword')}
              style={styles.forgotButton}
              labelStyle={styles.forgotButtonText}
            >
              비밀번호를 잊으셨나요?
            </Button>
          </View>
        </View>
      </SafeAreaView>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  background: {
    flex: 1,
  },
  container: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.4)',
  },
