          right={(props) => <List.Icon {...props} icon="chevron-right" />}
          onPress={() => navigation.navigate('Statistics')}
        />
        <Divider />

        <List.Item
          title="ì•Œë¦¼ ì„¤ì •"
          description="í‘¸ì‹œ ì•Œë¦¼ ë° ì´ë©”ì¼ ì„¤ì •"
          left={(props) => <List.Icon {...props} icon="bell" />}
          right={(props) => <List.Icon {...props} icon="chevron-right" />}
