        {renderStatsCard()}

        {/* ìµœê·¼ í™œë™ */}
        {renderRecentActivity()}

        {/* ë©”ë‰´ ë¦¬ìŠ¤íŠ¸ */}
        {renderMenuList()}

        <View style={styles.bottomSpacing} />
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
